&lt;li&gt;&lt;span class=&quot;EOP SCXW60411269 BCX0&quot; data-ccp-props=&quot;{&amp;quot;201341983&amp;quot;:0,&amp;quot;335559740&amp;quot;:240}&quot;&gt;&lt;span class=&quot;TextRun SCXW153930128 BCX0&quot; lang=&quot;EN-US&quot; data-contrast=&quot;auto&quot;&gt;&lt;span class=&quot;NormalTextRun SCXW153930128 BCX0&quot;&gt;Success in academics at a rigorous and highly regarded university: &lt;/span&gt;&lt;span class=&quot;NormalTextRun SCXW153930128 BCX0&quot;&gt;minimum&lt;/span&gt;&lt;span class=&quot;NormalTextRun SCXW153930128 BCX0&quot;&gt; GPA 3.5/4.0 or higher, standardized test scores minimum 95th percentile, &lt;/span&gt;&lt;span class=&quot;NormalTextRun SCXW153930128 BCX0&quot;&gt;merit-based&lt;/span&gt;&lt;span class=&quot;NormalTextRun SCXW153930128 BCX0&quot;&gt; scholarship recipient.&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;span class=&quot;EOP SCXW153930128 BCX0&quot; data-ccp-props=&quot;{&amp;quot;201341983&amp;quot;:0,&amp;quot;335559740&amp;quot;:240}&quot;&gt;&amp;nbsp;&lt;/span&gt;&amp;nbsp;&lt;/span&gt;&lt;/li&gt;
